mapper.insert:
INSERT INTO 
	app_request (app_no, emp_no) (
	SELECT 
		a.app_no, e.emp_no 
	FROM 
		approval a, employee e 
	WHERE 
		a.app_no = #{app_no}
		and e.emp_no = #{emp_no}
	)

mapper.update:
UPDATE 
	app_agree 
SET 
	agr_status = #{agr_status} 
WHERE 
	emp_no = #{emp_no} 
	and app_no = #{app_no}
