mapper.insert:
INSERT INTO 
	app_request (app_no, emp_no) (
	SELECT 
		a.app_no, e.emp_no 
	FROM 
		approval a, employee e 
	WHERE 
		a.app_no = #{app_no}
		and e.emp_no = #{emp_no}
	)

INSERT INTO
    app_agree (app_no, emp_no, agr_status) (
    SELECT 
        a.app_no, e.emp_no, a.app_status 
    FROM 
        approval a, employee e 
    WHERE 
        a.app_no = #{agrVo.app_no}
        and e.emp_no = (
            SELECT emp_no FROM employee 
            WHERE dept_no = (
                SELECT dept_no FROM employee WHERE emp_no = #{emp_no}
            ) 
            AND emp_position LIKE '%'||#{agrVo.emp_position}||'%'
        )
    )

mapper.update:
UPDATE 
	app_agree 
SET 
	agr_status = #{agr_status} 
WHERE 
	emp_no = #{emp_no} 
	and app_no = #{app_no}
